mapper.select:
select a.uid, a.username
from account a
         INNER JOIN account_contest_manager acm on a.uid = acm.uid
where acm.contest_id = #{contestId}
  and a.role = #{role}
limit #{pageSize} offset #{pageNum}

select count(*)
from account a
         INNER JOIN account_contest_manager acm on a.uid = acm.uid
where acm.contest_id = #{contestId}
  and a.role = #{role}
